    JKRSolidHeap * solidHeap = mDoExt_createSolidHeapToCurrent(0, NULL, 0);
    mDoGph_gInf_c::create();
    dComIfGd_init();
    mDoExt_adjustSolidHeap(solidHeap);
    mDoExt_restoreCurrentHeap();
    JFWAutoAbortGfx = mDoMain::developmentMode ? 0x02 : 0x01;
